[InfoPathEventHandler(EventType=InfoPathEventType.OnVersionUpgrade)]
public void OnVersionUpgrade(VersionUpgradeEvent e)
{
 thisXDocument.UI.Alert("The form version: " + e.<span class="label">DocumentVersion</span> + 
  "\nThe form template version: " + e.SolutionVersion);
 e.ReturnStatus = true;   
}

[InfoPathEventHandler(EventType=InfoPathEventType.OnVersionUpgrade)]
public void OnVersionUpgrade(VersionUpgradeEvent e)
{
 e.<span class="label">ReturnStatus</span> = false;   
}

[InfoPathEventHandler(EventType=InfoPathEventType.OnVersionUpgrade)]
public void OnVersionUpgrade(VersionUpgradeEvent e)
{
 thisXDocument.UI.Alert("The form version: " + e.DocumentVersion + 
  "\nThe form template version: " + e.<span class="label">SolutionVersion</span>);
 e.ReturnStatus = true;   
}

[InfoPathEventHandler(EventType=InfoPathEventType.OnVersionUpgrade)]
public void OnVersionUpgrade(VersionUpgradeEvent e)
{
 XDocument upgradeDocument = e.<span class="label">XDocument</span>;
}
