private string[] _forms = 
{
   @"\\MyServer\MyForms\MyForm.xsn", 
   @"\\MyServer\MyForms\manifest.xsf" 
};

private void someFunction()
{
   CacheFormTemplate(_forms);
}

private void CacheFormTemplate(string[] forms)
{
   foreach (string form in forms)
   {
      thisApplication.<span class="label">CacheSolution</span>(form);
    }
}

object objectReference = 1;

Microsoft.Office.Interop.InfoPath.SemiTrust.Application app = 
   thisApplication;

Microsoft.Office.Core.COMAddIns myCOMAddIns = 
   (Microsoft.Office.Core.COMAddIns)((_Application3)app).COMAddIns;

Microsoft.Office.Core.COMAddIn myCOMAddIn = 
   myCOMAddIns.Item(ref objectReference);

public void InstallForm()
{
   Microsoft.Office.Interop.InfoPath.Application infoPath = 
      new Microsoft.Office.Interop.InfoPath.ApplicationClass();
   infoPath.<span class="label">RegisterSolution</span>(@"C:\MyFormTemplate.xsn", "overwrite");
}

public void UninstallForm()
{
   Microsoft.Office.Interop.InfoPath.Application infoPath =
      new Microsoft.Office.Interop.InfoPath.ApplicationClass();
   infoPath.<span class="label">UnregisterSolution</span>(@"C:\My Forms\MyFormTemplate.xsn");
}
