private void AutomateInfoPathForm()
{
ExternalApplication infoPath = new ExternalApplicationClass();

 // Cache an InfoPath form template.
infoPath.<span class="label">CacheSolution</span>(@"\\MyServer\MyForms\MyForm.xsn");
}

private void AutomateInfoPathForm()
{
 ExternalApplication infoPath = new ExternalApplicationClass();
 // Open an InfoPath form.
 infoPath.Open(@"C:\My Forms\Form1.xml", 1);
 // Close the InfoPath form.
 infoPath.<span class="label">Close</span>(@"C:\My Forms\Form1.xml");
 // Quit the InfoPath application.
 infoPath.Quit();
}

private void CreateFromForm()
{
 ExternalApplication infoPath = new ExternalApplicationClass();

 // Create an InfoPath form.
 infoPath.<span class="label">New</span>(@"C:\My Forms\Form1.xml", 1);
}

private void CreateFromFormTemplate()
{
 ExternalApplication infoPath = new ExternalApplicationClass();

 // Create an InfoPath form.
 infoPath.<span class="label">NewFromSolution</span>(@"C:\My Forms\MyFormTemplate.xsn", 1);
}

